import React, { useState, useEffect } from 'react';
import { contactService } from '../../../services/apiContacts';
import { authService } from '../../../services/api';
import './css/ServiceContactsManager.css';

const ServiceContactsManager = ({ serviceId }) => {
  const [contacts, setContacts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingContact, setEditingContact] = useState(null);
  const [newContact, setNewContact] = useState({
    contact_type: 'phone',
    contact_value: '',
    label: ''
  });
  const [submitting, setSubmitting] = useState(false);

  const isAdmin = authService.isAdmin();
  const currentUser = authService.getCurrentUser();

  useEffect(() => {
    loadContacts();
  }, [serviceId]);

  const loadContacts = async () => {
    try {
      setLoading(true);
      const response = await contactService.getContactsForService(serviceId);
      setContacts(response.contacts);
    } catch (err) {
      setError('Error al cargar los contactos');
      console.error('Error loading contacts:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmitContact = async (e) => {
    e.preventDefault();
    if (!newContact.contact_value.trim()) {
      setError('El valor del contacto es requerido');
      return;
    }

    try {
      setSubmitting(true);
      if (editingContact) {
        await contactService.updateContact(serviceId, editingContact.id, newContact);
      } else {
        await contactService.createContact(serviceId, newContact);
      }

      setNewContact({ contact_type: 'phone', contact_value: '', label: '' });
      setShowAddForm(false);
      setEditingContact(null);
      await loadContacts();
    } catch (err) {
      setError(editingContact ? 'Error al actualizar el contacto' : 'Error al crear el contacto');
      console.error('Error saving contact:', err);
    } finally {
      setSubmitting(false);
    }
  };

  const handleEditContact = (contact) => {
    setEditingContact(contact);
    setNewContact({
      contact_type: contact.contact_type,
      contact_value: contact.contact_value,
      label: contact.label || ''
    });
    setShowAddForm(true);
  };

  const handleDeleteContact = async (contactId) => {
    if (!window.confirm('¿Está seguro de que desea eliminar este contacto?')) {
      return;
    }

    try {
      await contactService.deleteContact(serviceId, contactId);
      await loadContacts();
    } catch (err) {
      setError('Error al eliminar el contacto');
      console.error('Error deleting contact:', err);
    }
  };

  const getContactIcon = (type) => {
    switch (type) {
      case 'phone':
        return '📞';
      case 'email':
        return '✉️';
      case 'whatsapp':
        return '💬';
      case 'website':
        return '🌐';
      default:
        return '📋';
    }
  };

  const getContactLink = (type, value) => {
    switch (type) {
      case 'phone':
        return `tel:${value}`;
      case 'email':
        return `mailto:${value}`;
      case 'whatsapp':
        return `https://wa.me/${value.replace(/\D/g, '')}`;
      case 'website':
        return value.startsWith('http') ? value : `https://${value}`;
      default:
        return '#';
    }
  };

  if (loading) {
    return <div className="contacts-section">Cargando contactos...</div>;
  }

  return (
    <div className="contacts-section">
      <div className="contacts-header">
        <h4>Líneas de Contacto</h4>
        {(isAdmin || currentUser) && (
          <button
            className="add-contact-button"
            onClick={() => {
              setShowAddForm(true);
              setEditingContact(null);
              setNewContact({ contact_type: 'phone', contact_value: '', label: '' });
            }}
          >
            Agregar Contacto
          </button>
        )}
      </div>

      {error && (
        <div className="error-message">
          {error}
          <button onClick={() => setError(null)}>×</button>
        </div>
      )}

      {/* Add/Edit Contact Form */}
      {showAddForm && (
        <form className="add-contact-form" onSubmit={handleSubmitContact}>
          <div className="form-group">
            <label htmlFor="contact-type">Tipo:</label>
            <select
              id="contact-type"
              value={newContact.contact_type}
              onChange={(e) => setNewContact(prev => ({ ...prev, contact_type: e.target.value }))}
              required
            >
              <option value="phone">Teléfono</option>
              <option value="email">Email</option>
              <option value="whatsapp">WhatsApp</option>
              <option value="website">Sitio Web</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="contact-value">Valor:</label>
            <input
              id="contact-value"
              type={newContact.contact_type === 'email' ? 'email' : 'text'}
              value={newContact.contact_value}
              onChange={(e) => setNewContact(prev => ({ ...prev, contact_value: e.target.value }))}
              placeholder={
                newContact.contact_type === 'phone' ? 'Ej: +1234567890' :
                newContact.contact_type === 'email' ? 'Ej: contacto@ejemplo.com' :
                newContact.contact_type === 'whatsapp' ? 'Ej: +1234567890' :
                'Ej: www.ejemplo.com'
              }
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="contact-label">Etiqueta (opcional):</label>
            <input
              id="contact-label"
              type="text"
              value={newContact.label}
              onChange={(e) => setNewContact(prev => ({ ...prev, label: e.target.value }))}
              placeholder="Ej: Oficina principal, Soporte técnico"
            />
          </div>

          <div className="form-actions">
            <button type="submit" disabled={submitting} className="submit-button">
              {submitting ? (editingContact ? 'Actualizando...' : 'Agregando...') :
               (editingContact ? 'Actualizar Contacto' : 'Agregar Contacto')}
            </button>
            <button
              type="button"
              onClick={() => {
                setShowAddForm(false);
                setEditingContact(null);
                setNewContact({ contact_type: 'phone', contact_value: '', label: '' });
              }}
              className="cancel-button"
            >
              Cancelar
            </button>
          </div>
        </form>
      )}

      {/* Contacts List */}
      <div className="contacts-list">
        {contacts.length === 0 ? (
          <div className="no-contacts">
            No hay líneas de contacto registradas.
          </div>
        ) : (
          contacts.map((contact) => (
            <div key={contact.id} className="contact-item">
              <div className="contact-info">
                <div className="contact-icon">
                  {getContactIcon(contact.contact_type)}
                </div>
                <div className="contact-details">
                  <div className="contact-value">
                    <a
                      href={getContactLink(contact.contact_type, contact.contact_value)}
                      target={contact.contact_type === 'website' ? '_blank' : undefined}
                      rel={contact.contact_type === 'website' ? 'noopener noreferrer' : undefined}
                    >
                      {contact.contact_value}
                    </a>
                  </div>
                  {contact.label && (
                    <div className="contact-label">{contact.label}</div>
                  )}
                </div>
              </div>

              {(isAdmin || currentUser) && (
                <div className="contact-actions">
                  <button
                    onClick={() => handleEditContact(contact)}
                    className="edit-contact-button"
                    title="Editar contacto"
                  >
                    ✏️
                  </button>
                  <button
                    onClick={() => handleDeleteContact(contact.id)}
                    className="delete-contact-button"
                    title="Eliminar contacto"
                  >
                    🗑️
                  </button>
                </div>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default ServiceContactsManager;
