import React, { useState, useEffect } from 'react';
import reviewService from '../../services/apiReviews';
import { authService } from '../../services/api';
import './css/ServiceReviewsManager.css';

const ServiceReviewsManager = ({ serviceId, avgRating, reviewsCount }) => {
  const [reviews, setReviews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showAddForm, setShowAddForm] = useState(false);
  const [newReview, setNewReview] = useState({
    rating: 5,
    title: '',
    comment: ''
  });
  const [submitting, setSubmitting] = useState(false);

  const currentUser = authService.getCurrentUser();
  const isAdmin = authService.isAdmin();

  useEffect(() => {
    loadReviews();
  }, [serviceId]);

  const loadReviews = async () => {
    try {
      setLoading(true);
      const response = await reviewService.getReviewsForService(serviceId);
      setReviews(response.reviews);
    } catch (err) {
      setError('Error al cargar las reseñas');
      console.error('Error loading reviews:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmitReview = async (e) => {
    e.preventDefault();
    if (!newReview.title.trim() || !newReview.comment.trim()) {
      setError('El título y comentario son requeridos');
      return;
    }

    try {
      setSubmitting(true);
      await reviewService.createReview(serviceId, newReview);
      setNewReview({ rating: 5, title: '', comment: '' });
      setShowAddForm(false);
      await loadReviews();
    } catch (err) {
      setError('Error al crear la reseña');
      console.error('Error creating review:', err);
    } finally {
      setSubmitting(false);
    }
  };

  const handleDeleteReview = async (reviewId) => {
    if (!window.confirm('¿Está seguro de que desea eliminar esta reseña?')) {
      return;
    }

    try {
      await reviewService.deleteReview(reviewId);
      await loadReviews();
    } catch (err) {
      setError('Error al eliminar la reseña');
      console.error('Error deleting review:', err);
    }
  };

  const renderStars = (rating, interactive = false, onChange = null) => {
    return (
      <div className="stars-container">
        {[1, 2, 3, 4, 5].map((star) => (
          <span
            key={star}
            className={`star ${star <= rating ? 'filled' : 'empty'} ${interactive ? 'interactive' : ''}`}
            onClick={interactive ? () => onChange(star) : undefined}
          >
            ★
          </span>
        ))}
      </div>
    );
  };

  if (loading) {
    return <div className="reviews-section">Cargando reseñas...</div>;
  }

  return (
    <div className="reviews-section">
      <div className="reviews-header">
        <h4>Reseñas ({reviewsCount})</h4>
        <div className="rating-summary">
          {renderStars(Math.round(avgRating || 0))}
          <span className="rating-text">
            {avgRating ? avgRating.toFixed(1) : 'Sin calificación'}
          </span>
        </div>
      </div>

      {error && (
        <div className="error-message">
          {error}
          <button onClick={() => setError(null)}>×</button>
        </div>
      )}

      {/* Add Review Form */}
      {currentUser && !showAddForm && (
        <button
          className="add-review-button"
          onClick={() => setShowAddForm(true)}
        >
          Agregar Reseña
        </button>
      )}

      {showAddForm && (
        <form className="add-review-form" onSubmit={handleSubmitReview}>
          <div className="form-group">
            <label>Calificación:</label>
            {renderStars(newReview.rating, true, (rating) =>
              setNewReview(prev => ({ ...prev, rating }))
            )}
          </div>

          <div className="form-group">
            <label htmlFor="review-title">Título:</label>
            <input
              id="review-title"
              type="text"
              value={newReview.title}
              onChange={(e) => setNewReview(prev => ({ ...prev, title: e.target.value }))}
              placeholder="Título de tu reseña"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="review-comment">Comentario:</label>
            <textarea
              id="review-comment"
              value={newReview.comment}
              onChange={(e) => setNewReview(prev => ({ ...prev, comment: e.target.value }))}
              placeholder="Escribe tu reseña aquí..."
              rows={4}
              required
            />
          </div>

          <div className="form-actions">
            <button type="submit" disabled={submitting} className="submit-button">
              {submitting ? 'Enviando...' : 'Enviar Reseña'}
            </button>
            <button
              type="button"
              onClick={() => setShowAddForm(false)}
              className="cancel-button"
            >
              Cancelar
            </button>
          </div>
        </form>
      )}

      {/* Reviews List */}
      <div className="reviews-list">
        {reviews.length === 0 ? (
          <div className="no-reviews">
            No hay reseñas aún. ¡Sé el primero en reseñar este servicio!
          </div>
        ) : (
          reviews.map((review) => (
            <div key={review.id} className="review-item">
              <div className="review-header">
                <div className="review-user">
                  <strong>{review.user_name}</strong>
                  <span className="review-date">
                    {new Date(review.created_at).toLocaleDateString()}
                  </span>
                </div>
                <div className="review-rating">
                  {renderStars(review.rating)}
                </div>
              </div>

              {review.title && (
                <h5 className="review-title">{review.title}</h5>
              )}

              {review.comment && (
                <p className="review-comment">{review.comment}</p>
              )}

              {(isAdmin || currentUser?.id === review.user_id) && (
                <div className="review-actions">
                  <button
                    onClick={() => handleDeleteReview(review.id)}
                    className="delete-review-button"
                    title="Eliminar reseña"
                  >
                    🗑️
                  </button>
                </div>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default ServiceReviewsManager;
